package zork.rooms;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import zork.Door;import zork.Enemy;import zork.Item;import java.util.ArrayList;import java.util.HashMap;public class Room {		private String description;	private HashMap<String, Door> exits;	private ArrayList<Enemy> enemys;	private ArrayList<Item> items;	public Room(String description) {		this.description = description;		this.exits = new HashMap<>();		this.enemys = new ArrayList<>();		this.items = new ArrayList<>();	}	public void setExits(Door north, Door east, Door south, Door west) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public String shortDescription() {		return description;	}	public String longDescription() {		StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");		stringBuilder.append(exitString());		return stringBuilder.toString();	}	private String exitString() {		return "Exits:" + String.join(" ", exits.keySet());	}	public Room nextRoom(String direction) {		return exits.get(direction).getOtherRoom(this);	}	public void addItem (Item item) {		items.add(item);	}	public void addEnemy (Enemy enemy) {		enemys.add(enemy);	}}